function TIC()
	
	vbank(1)
	
	cls(0)
	
	vbank(0)
	
	--cls()
	
	map(0,0,30,17,0,0,0)
	
	local x, y = mouse()
	
	for i=0, 360,0.2 do
		
		local x2, y2 = x+40*math.sin(math.rad(i))+0.5,y+40*math.cos(math.rad(i))+0.5
		
		local x3, y3 = x, y
		
		local offset = smoothFollow(x, y, x2, y2, 40)
		
		for a=1, 40 do
			
			x3, y3 = x3 - offset.xOff, y3 - offset.yOff
			
			if pix(x3,y3) ~= 0 then
				
				x2, y2 = x3-offset.xOff*0, y3+offset.yOff*0
				
				break
				
			end
			
		end
		
		vbank(1)
		
		line(x, y, x2, y2, 12)
		
		vbank(0)
		
	end
	
	cls(0)
	
end

function smoothFollow(x1,y1,x2,y2,dist)
	
		--dist = math.sqrt((x2 - x1) ^ 2 + (y2 - y1) ^ 2)
	
	return {xOff=((x1-x2+0.5)/dist),yOff=((y1-y2+0.5)/dist)}
	
end
